INSERT INTO REVIEW_BOARD (
	BOARD_NUM
	, ITEM_CODE
	, BOARD_TITLE
	, BOARD_CONTENT
	, MEMBER_ID
	, STAR_POINT
) VALUES (
	(SELECT NVL(MAX(BOARD_NUM), 0) + 1 FROM REVIEW_BOARD)
	, #{itemCode}
	, #{boardTitle}
	, #{boardContent}
	, #{memberId}
	, #{starPoint}
)


	SELECT 'REVIEW_IMG_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(REVIEW_IMG_CODE, 12))), 0) + 1 , 3, 0)
	FROM REVIEW_IMG

INSERT INTO REVIEW_IMG (
	REVIEW_IMG_CODE
	, ORIGIN_NAME
	, SAVED_NAME
	, ITEM_CODE
) VALUES (
	#{reviewImgVO.reviewImgCode}
	, #{reviewImgVO.originName}
	, #{reviewImgVO.savedName}
	, #{itemCode}
)

INSERT INTO REVIEW_REPLY (
	REPLY_NUM
	, REPLY_CONTENT
	, MEMBER_ID
	, BOARD_NUM
	, ITEM_CODE
) VALUES (
	(SELECT NVL(MAX(REPLY_NUM), 0) + 1 FROM REVIEW_REPLY)
	, #{replyContent}
	, #{memberId}
	, (SELECT BOARD_NUM
		FROM REVIEW_BOARD
		WHERE ITEM_CODE = #{itemCode})
	, #{itemCode}
)
