mapper.select:
SELECT SUB_CATE_CODE ,SUB_CATE_NAME ,MAIN_CATE_CODE ,(SELECT MAIN_CATE_NAME FROM MAIN_CATE WHERE MAIN_CATE_CODE=SUB_CATE.MAIN_CATE_CODE) MAIN_CATE_NAME FROM SUB_CATE
WHERE MAIN_CATE_CODE=#{mainCateCode}
ORDER BY SUB_CATE_CODE

SELECT DETAIL_CATE_CODE ,DETAIL_CATE_NAME ,SUB_CATE_CODE ,(SELECT SUB_CATE_NAME FROM SUB_CATE WHERE SUB_CATE_CODE=DETAIL_CATE.SUB_CATE_CODE) SUB_CATE_NAME FROM DETAIL_CATE
WHERE SUB_CATE_CODE=#{subCateCode}
ORDER BY DETAIL_CATE_CODE

mapper.insert:

    SELECT 'MAIN_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(MAIN_CATE_CODE, 6))), 0) +1, 3, 0)
    FROM MAIN_CATE

INSERT INTO MAIN_CATE (
    MAIN_CATE_CODE
    ,MAIN_CATE_NAME
) VALUES(
    #{mainCateCode}
    ,#{mainCateName}
)


    SELECT 'SUB_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(SUB_CATE_CODE, 5))), 0) +1, 3, 0)
    FROM SUB_CATE

INSERT INTO SUB_CATE (
    SUB_CATE_CODE
    ,SUB_CATE_NAME
    ,MAIN_CATE_CODE
) VALUES(
    #{subCateCode}
    ,#{subCateName}
    ,#{mainCateCode}
)


    SELECT 'DETAIL_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(DETAIL_CATE_CODE, 8))), 0) +1, 4, 0)
    FROM DETAIL_CATE

INSERT INTO DETAIL_CATE (
    DETAIL_CATE_CODE
    ,DETAIL_CATE_NAME
    ,SUB_CATE_CODE
) VALUES(
    #{detailCateCode}
    ,#{detailCateName}
    ,#{subCateCode}
)
