mapper.insert:
INSERT INTO SHOP_ITEM (
	  ITEM_CODE
	  , ITEM_NAME
	  , ITEM_PRICE
	  , ITEM_COMMENT
	  , ITEM_STATUS
	  , TRADE_TYPE
	  , TRADE_ADDR
	  , MAIN_CATE_CODE

	 	 , SUB_CATE_CODE


	 	 , DETAIL_CATE_CODE

	  , MEMBER_ID
  ) VALUES (
  	  #{itemCode}	
      , #{itemName}
      , #{itemPrice}
      , #{itemComment}
      , #{itemStatus}
      , #{tradeType}
      , #{tradeAddr}
      , #{mainCateCode}

      	, #{subCateCode}


     	 , #{detailCateCode}

      , #{memberId}
)

INSERT INTO ITEM_IMG (
		IMG_CODE
		, ORIGIN_NAME
		, ATTACHED_NAME
		, IS_MAIN
		, ITEM_CODE
	) 

SELECT (SELECT 'IMG_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(IMG_CODE, 5))), 0) +1 + #{i}, 3, 0) FROM ITEM_IMG)
		, #{img.originName}
		, #{img.attachedName}
		, #{img.isMain}
		, #{img.itemCode}
FROM DUAL

mapper.select:
SELECT ROW_NUM ,ITEM_CODE ,ITEM_NAME ,ITEM_PRICE ,ATTACHED_NAME FROM ( SELECT ROWNUM ROW_NUM ,ITEM_CODE ,ITEM_NAME ,ITEM_PRICE ,ATTACHED_NAME FROM ( SELECT ITEM.ITEM_CODE ,ITEM_NAME ,ITEM_PRICE ,ATTACHED_NAME FROM SHOP_ITEM ITEM,ITEM_IMG IMG WHERE IS_MAIN ='Y' AND ITEM.ITEM_CODE=IMG.ITEM_CODE
AND MAIN_CATE_CODE=#{mainCateCode}
AND SUB_CATE_CODE=#{subCateCode}
AND DETAIL_CATE_CODE=#{detailCateCode}
ORDER BY ITEM_CODE DESC )) WHERE ROW_NUM >= #{startNum} AND ROW_NUM <= #{endNum}

mapper.update:
UPDATE SHOP_ITEM
SET 
	ITEM_NAME = #{itemName}
	, ITEM_PRICE = #{itemPrice}
	, ITEM_COMMENT = #{itemComment}
	, ITEM_STATUS = #{itemStatus}
	, TRADE_TYPE = #{tradeType}
	, TRADE_ADDR = #{tradeAddr}
WHERE ITEM_CODE = #{itemCode}

mapper.select:
SELECT COUNT(ITEM_CODE) FROM SHOP_ITEM


WHERE MAIN_CATE_CODE=#{mainCateCode}


WHERE SUB_CATE_CODE=#{subCateCode}


WHERE DETAIL_CATE_CODE=#{detailCateCode}
