mapper.insert:
INSERT INTO SHOP_ITEM (
	  ITEM_CODE
	  , ITEM_NAME
	  , ITEM_PRICE
	  , ITEM_STOCK
	  , ITEM_COMMENT
	  , ITEM_STATUS
	  , TRADE_TYPE
	  , TRADE_ADDR
  ) VALUES (
  	  #{itemCode}	
      , #{itemName}
      , #{itemPrice}
      , #{itemStock}
      , #{itemComment}
      , #{itemStatus}
      , #{tradeType}
      , #{tradeAddr}
)

INSERT INTO ITEM_IMG (
		IMG_CODE
		, ORIGIN_NAME
		, ATTACHED_NAME
		, IS_MAIN
		, ITEM_CODE
	) 

SELECT (SELECT 'IMG_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(IMG_CODE, 5))), 0) +1 + #{i}, 3, 0) FROM ITEM_IMG)
		, #{img.originName}
		, #{img.attachedName}
		, #{img.isMain}
		, #{img.itemCode}
FROM DUAL
