mapper.insert:
INSERT INTO SHOP_ITEM (
	  ITEM_CODE
	  , ITEM_NAME
	  , ITEM_PRICE
	  , ITEM_STOCK
	  , ITEM_COMMENT
	  , ITEM_STATUS
	  , TRADE_TYPE
  ) VALUES (
      (SELECT 'ITEM_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(ITEM_CODE, 6))), 0) +1, 3, 0) FROM SHOP_ITEM)
      , #{itemName}
      , #{itemPrice}
      , #{itemStock}
      , #{itemComment}
      , #{itemStatus}
      , #{tradeType}
)
