mapper.insert:
INSERT INTO SHOP_MEMBER(
			 MEMBER_ID
		    , MEMBER_PW
			, MEMBER_NAME
			, MEMBER_NICKNAME
			, MEMBER_ADDR
			, ADDR_DETAIL
			, MEMBER_TELL
			, MEMBER_EMAIL
	    ) VALUES(
		     #{memberId}
		    , #{memberPw}
		    , #{memberName}
		    , #{memberNickName}
		    , #{memberAddr}
		    , #{addrDetail}
		    , #{memberTell}
		    , #{memberEmail}
	    )

mapper.select:
SELECT MEMBER_ID
	, MEMBER_PW
	, MEMBER_ROLE
FROM SHOP_MEMBER
WHERE MEMBER_ID = #{memberId}
AND MEMBER_PW = #{memberPw}
